/* ==UserStyle==
@name           Jaden and Yubel
@version        2.0.0
@namespace      url(https://www.w3.org/1999/xhtml/)
@description
Jaden and Yubel v2.0 (featuring Chatbox and other scrolling fixes)
Inspired by O OO/The XAZ.
Special thanks to Skeletonxf for finally coming up with a chatbox and getting me back into this. This style wouldn't have a licence without you.
Open for testing and feedback. Potentially compatible with any resolution on almost any device.
For optimal viewing, press F11.
@author         Userstyle Â© 2017 Yubelious! under MIT liecence.
==/UserStyle== */

@import url('https://fonts.googleapis.com/css?family=Berkshire+Swash|Noto+Sans|Oxygen');

body {
    font-size: 0;
    font-family: "Geneva", Ariel, sans-serif;
    line-height: 1;
    color: rgba(193, 158, 77, 0.9);
    background-color: rgba(0, 0, 0, 0.1);
}
html {
    background: url(https://i62.servimg.com/u/f62/17/89/30/88/57448310.jpg) #766D87 0 3vw/cover fixed; /*Image by the amazing Moribuden*/
}
/*Here are things that oddly starts with a period. I spent all my life thinking that full stops go at the end. And here I am starting with them.*/
.loadmore, hr {
    display: none;
}
.h2, h2 {
    font-size: 2.5vw;
    font-style: italic;
    margin: 15px 0 15px 0;
    padding-top: 3.5vw;
}
.h3, h3 {
    color: antiquewhite;
    font-size: 1.5vw;
    font-style: italic;
    text-shadow: 0 0 0.5vw rgba(0, 0, 0, 0.75), 0 0 0.5vw rgba(0, 0, 0, 0.75), 0 0 0.5vw rgba(0, 0, 0, 0.75);
    background: linear-gradient(to right, rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0));
    height: 2vw;
    width: 100%;
    margin: 0;
    padding: 0.5vw 0 0 0.5vw;
}
.h4, h4 {
    font-size: 2vw;
    color: #30a791;
    text-shadow: 0.5px 0.5px 0.5px rgba(48, 167, 145, 0.6), 1px 1px 1px rgba(193, 158, 77, 0.7), 0.7px 0.7px 0.7px rgba(0, 0, 0, 0.5);
}
.h6, h6 {
    font-size: 1vw;
    color: antiquewhite;
}
.btn {
    font-weight: bold;
}
.checkbox label {
    font-size: 1.1vw;
    line-height: 22px;
}
.checkbox label:hover {
    color: #C19E4D
}
.container {
    width: 100%;
}
.profile-if-not-contributor {
    font-size: 1vw;
    margin: 0;
    padding: 1vw;
}
.table {
    text-align: justify;
    background:  black;
    opacity: 1;
    float: right;
    width: 75%;
    margin: 9vh 0.1vw 10vh -0.1vw;
    z-index: 1;
    position: relative;
    right: 12.5%;
}
.table::before {
    content: "Get Your Game On!";
    color: antiquewhite;
    text-align: center;
    background-size: 100%;
    opacity: 0.75;
    z-index: -1;
    position: absolute;
    top: -1.5vw;
    left: 0;
    bottom: 0;
    right: 0; 
}
/*Here we have some general button rules.*/
#tab-login .button, #tab-register .button, #tab-news .button, #tab-duel .button, #tab-decks .button, #tab-profile .button {
    -moz-transition-duration: 2s;
    -o-transition-duration: 2s;
    transition-duration: 2s;
    text-align: center;
    font-weight: bold;
    border: 2px solid;
    overflow: hidden;
}
#tab-login .button:hover, #tab-register .button:hover, #tab-duel .button:hover, #tab-decks .button:hover, #tab-profile .button:hover {
    -moz-transition-duration: 2s;
    -o-transition-duration: 2s;
    transition-duration: 2s;
    cursor: pointer;
    overflow: hidden;
}
/*This is the code for the navbar, which can be seen from any tab.*/
.container-fluid {
    background-color: #766D87;
    height: 3vw;
    min-height: 0;
    max-height: 25vh;
    width: 100vw;
    padding-left: 20vw;
    margin-right: auto;
    margin-left: auto;
    z-index: 2;
    position: fixed;
    top: 0;
}
.navbar-text {
    font-size: 1vw;
    color: antiquewhite;
    line-height: 3;
    margin: 0 0 0 3vw;
}
.navbar-nav>li>.menu-link {
    -moz-transition: color 2s ease-in-out, border 2s ease-in-out;
    -o-transition: color 2s ease-in-out, border 2s ease-in-out;
    transition: color 2s ease-in-out, border 2s ease-in-out;
    font-size: 0.8vw;
    line-height: 1vw;
    color: rgba(193, 158, 77, 0.9) !important;
    text-shadow: none;
    border: 1px dotted rgba(193, 158, 77, 0.9);
    height: 3vw;
    min-height: 0;
    max-height: 25vh;
    width:auto;
    margin-left: 0.25vw;
    padding: 1vw;
}
.nav>li>a:focus {
    -moz-transition: color 2s ease-in-out, border 2s ease-in-out;
    -o-transition: color 2s ease-in-out, border 2s ease-in-out;
    transition: color 2s ease-in-out, border 2s ease-in-out;
    color: rgba(48, 167, 145, 0.9) !important;
    font-style: italic;
    background-color: rgba(118, 109, 135, 0.8);
    border-color: rgba(48, 167, 145, 0.9);
}
.nav>li>a:hover {
    -moz-transition: color 2s ease-in-out, border 2s ease-in-out;
    -o-transition: color 2s ease-in-out, border 2s ease-in-out;
    transition: color 2s ease-in-out, border 2s ease-in-out;
    color: rgba(48, 167, 145, 0.9) !important;
    background-color: rgba(118, 109, 135, 0.8);
    border-color: rgba(48, 167, 145, 0.9);
}
.nav>li.disabled>.menu-link, .nav>li.disabled>.menu-link:focus,.nav>li.disabled>.menu-link:hover {
    color: rgba(193, 158, 77, 0.5) !important;
    background-image: repeating-linear-gradient(0deg, transparent, transparent 0.9vw, rgba(150, 150, 150, 0.8) 2vw, rgba(160, 160, 160, 0.4) 0.5vw, transparent 1vw), linear-gradient( to bottom, rgba(78, 78, 78, 0.6), rgba(0, 0, 0, 0.6));
    border: none;
}
#cf_alert_div {
    height: 6vw;
    overflow-y: hidden;
    margin: 0;
    padding: 0;
    top: 3vw !important;
}
#cf_alert_div a, #cf_alert_div p{
    font-size: 1vw !important;
}
#link-chat {
    -moz-transition: color 2s ease-in-out, border 2s ease-in-out;
    -o-transition: color 2s ease-in-out, border 2s ease-in-out;
    transition: color 2s ease-in-out, border 2s ease-in-out;
    font-size: 0.8vw;
    font-weight: bold;
    line-height: 1vw;
    color: antiquewhite;
    background-color: black;
    border: 1px dotted antiquewhite;
    cursor: help;
    height: 3vw;
    width: auto;
    margin-left: 0.25vw;
    padding: 1vw;
}
#link-chat:focus, #link-chat:hover {
    -moz-transition: color 2s ease-in-out,border 2s ease-in-out;
    -o-transition: color 2s ease-in-out, border 2s ease-in-out;
    transition: color 2s ease-in-out, border 2s ease-in-out;
    background-color: rgba(0, 0, 0, 0.8);
    border: 1px dashed;
}

/*If it shows up on the Login or Register tab, it's in here.*/
.alert-success, .alert-danger {
    background: radial-gradient(silver, rgba(169, 169, 169, 0.75), rgba(169, 169, 169, 0.5)) !important;
}
.form-control {
    -moz-transition-duration: 1s;
    -o-transition-duration: 1s;
    transition-duration: 1s;
    -moz-transition-property: all;
    -o-transition-property: all;
    transition-property: all;
    font-size: 1.25vw;
    color: rgba(118, 109, 135, 0.7);
    text-indent: 0.5vw;
    background-color: rgba(250, 235, 215, 0.7);
    border: 4px solid #C19E4D;
    box-shadow: 0px 1px 1px rgba(48, 167, 145, 0.4), 0px 0px 8px rgba(193, 158, 77, 0.8), 0px 1px 8px rgba(0, 0, 0, 0.6);
    height: 2.75vw;
    padding: 0;
}
.form-control:hover {
    -moz-transition-duration: 1s;
    -o-transition-duration: 1s;
    transition-duration: 1s;
    -moz-transition-property: all;
    -o-transition-property: all;
    transition-property: all;
    color: #766D87;
    background-color: antiquewhite;
    border: 4px solid #C19E4D;
    box-shadow: 0px 1px 1px rgba(48, 167, 145, 0.6), 0px 0px 8px rgba(193, 158, 77, 1), 0px 1px 8px rgba(0, 0, 0, 0.8);
}
.form-control:focus {
    -moz-transition-duration: 1s;
    -o-transition-duration: 1s;
    transition-duration: 1s;
    -moz-transition-property: all;
    -o-transition-property: all;
    transition-property: all;
    color: rgba(118, 109, 135, 0.9);
    background-color: rgba(250, 235, 215, 0.3);
    border: 4px solid #30A791;
    box-shadow: 0px 1px 1px rgba(193, 158, 77, 0.6), 0px 0px 8px rgba(48, 167, 145, 1), 0px 1px 8px rgba(0, 0, 0, 0.8);
}
.form-group {
    font-size: 1.5vw;
    margin-bottom: 5vh;
}
#alerts {
    font-size: 1vw !important;
    padding-top: 2.5vw;
    z-index: 2;
    position: fixed;
    right: 2vw;
}
#alerts .close {
    font-size: 1vw;
    margin:0;
    padding: 0;
}
#login-create-label, #login-recover-label, #register-login-label {
    -moz-transition: all 1s ease-in-out;
    -o-transition: all 1s ease-in-out;
    transition: all 1s ease-in-out;
    font-size: 1vw;
    color: rgba(193, 158, 77, 0.9) !important;
    text-shadow: 0px 1px 0.5px rgba(193, 158, 77, 0.6), 0px 0px 2px rgba(48, 167, 145, 1), 0px 1px 2px rgba(0, 0, 0, 0.8);
    float: right;
    padding: 1vw;
}
#login-create-label, #login-recover-label {
    margin: 1.25vw 18.75vw 0 0;
}
#register-login-label {
    margin: -2vw 4vw 0 0;
}
#login-create-label:hover, #login-recover-label:hover, #register-login-label:hover, #login-create-label:focus, #login-recover-label:focus, #register-login-label:focus {
    -moz-transition: all 500ms ease-in-out;
    -o-transition: all 500ms ease-in-out;
    transition: all 500ms ease-in-out;
    color: rgba(48, 167, 145, 0.9) !important;
    text-shadow: 0px 0.5px 0.5px rgba(48, 167, 145, 0.4), 0px 0px 2px rgba(193, 158, 77, 0.8), 0px 0.5px 2px rgba(0, 0, 0, 0.6);
}
#login-form label {
    cursor: default;
}
#login-remember {
    cursor: pointer;
}
#tab-login .container, #tab-register .container {    
    background: url(https://i62.servimg.com/u/f62/17/89/30/88/55637910.jpg) 0 -1.5vw/75vw 40vw;
    height: 37vw;
    width: 75vw;
    float: none;
}

/*This affected the news tab. You won't be able to see this or the other tabs (except Login and Register) until you log in.*/
#newsMain {
    font-size: 1vw;
    padding-bottom: 40px; /*Increase this value if the taskbar blocks the interface on the news tab.*/
}
#tab-news .button {
    font-size: 1.25vw;
    animation: fadein 2s;
    color: rgba(250, 235, 215, 0.5);
    text-shadow: 1px 1px 1px rgba(250, 235, 215, 0.5), 2px 2px 2px rgba(250, 235, 215, 0.25), 3px 3px 3px rgba(250, 235, 215, 0.125);
    border-color: rgba(250, 235, 215, 0.5);
    height: auto;
    min-height: 0;
    width: auto;
    min-width: 0;
    padding: 0.1vw 1vw 0.1vw 0.25vw;
}
#tab-news .button:hover {
    color: antiquewhite;
    text-shadow: 1px 1px 1px rgba(250, 235, 215, 1), 2px 2px 2px rgba(250, 235, 215, 0.5), 3px 3px 3px rgba(250, 235, 215, 0.25);
    border-color: antiquewhite;
}
#tab-news .button:focus {
    color: rgba(250, 235, 215, 0.25);
    text-shadow: 1px 1px 1px rgba(250, 235, 215, 0.25), 2px 2px 2px rgba(250, 235, 215, 0.125), 3px 3px 3px rgba(250, 235, 215, 0.0625);
    background-color: rgba(200, 200, 200, 0.25);
    border-color: rgba(250, 235, 215, 0.25);
}
@keyframes fadein {
    from {opacity: 0;}
    to {opacity: 0.85;}
}

/*This affects the Duel tab, but not the duel pages.*/
div#tab-duel-home {
    overflow: hidden;
}
#tab-duel .button {
    font-size: 1.75vw;
    text-shadow: 0 0 0.5vw rgba(0, 0, 0, 0.25), 0 0 0.5vw rgba(0, 0, 0, 0.25), 0 0 0.5vw rgba(0, 0, 0, 0.25);
    background-color: rgba(0, 0, 0, 0.75);
    border-color: rgba(193, 158, 77, 0.75);
    padding: 1px;
}
#tab-duel .button:focus {
    font-style :italic;
}
#tab-duel .button:hover {
    color: antiquewhite;
    text-shadow: 0 0 0.5vw rgba(0, 0, 0, 0.75), 0 0 0.5vw rgba(0, 0, 0, 0.75), 0 0 0.5vw rgba(0, 0, 0, 0.75);
    background-color: rgba(192, 192, 192, 0.25);
}
#tab-duel div.profile-if-not-contributor {
    background: radial-gradient(silver, rgba(0, 0, 0, 0.75), rgba(0, 0, 0, 0.5)) !important;
    border: none !important;
    margin: 0 !important;
}
#tab-duel .progress {
    background: linear-gradient(to right, silver, darkgray, rgba(169, 169, 169, 0));
    margin: 0;
    padding: 0;    
}
/*This more specifically affects the dueling menu of the duel tab.*/
#tab-duel-home .button {
    color: #c19e4d;
    width: 100%;
    margin-top: 2vw; /*Reduce this number if the taskbar covers the interface on the duel home tab, or change your resolution.*/
}
#tab-duel-home .mlabel:before {
    content: " ";
    height: 2vw;
    width: 100vw;
    background: linear-gradient(to right, rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0));
    z-index: -1;
    position: absolute;
    left: 0;
}
#tab-duel-home .mlabel {
    font-size: 1.5vw;
    font-weight: bold;
    text-shadow: 0 0 0.125vw rgba(0, 0, 0, 0.9), 0 0 0.125, #766D87;
}
#tab-duel-home p {
    color: antiquewhite;
    font-size: 1.5vw;
    text-align: left !important;
    text-shadow: 0 0 0.5vw rgba(0, 0, 0, 0.9), 0 0 0.5vw rgba(0, 0, 0, 0.9), 0 0 0.5vw rgba(0, 0, 0, 0.9);
    width: 100%;
    padding: 1vw;
}
/*This affects the searching pages, such as bot duels.*/
#tab-duel-searching .button {
    color: rgba(48, 167, 145, 0.9);
    border-color: rgba(48, 167, 145, 0.9);
    opacity: 0.9;
    width: 100%;
    margin-top: 20vh;
}
#tab-duel-searching .button:focus {
    transition-duration: 100ms;
    background: url(http://www.wallpapermaiden.com/wallpaper/5402/download/1920x1080/yu-gi-oh-gx-yubel-jaden-yuki-beyond-the-clouds.jpg) no-repeat center center fixed !important; /*Image by either Dyunasumon or Moribuden.*/
    opacity: 1;
    height: 105vh;
    width: 110%;
    z-index: 5;
    margin-top: 0vh;
    position: fixed;
    top: 0;
    left: 0;
}
#tab-duel-searching .button:hover {
    background: url(http://www.wallpapermaiden.com/wallpaper/5402/download/1920x1080/yu-gi-oh-gx-yubel-jaden-yuki-beyond-the-clouds.jpg) no-repeat center center fixed; /*Image by either Dyunasumon or Moribuden.*/
}
/*This affects the game list page.*/
button#lobby-duels-hide-gamelist {
    font-size: 2vw;
    width: 100%;
    margin: 6vh -30vw -2px -2px;
}
#tab-duel-gamelist .button {
    color: #c19e4d;
}
#tab-duel-gamelist .button:not(#lobby-duels-hide-gamelist) {
    font-size: 1.25vw;
    width: 50.07%;
    margin: 0 -0.3vw -2px -2px;
    padding: 1px;
}
#tab-duel-gamelist .container {
    width: 100%;
}
#tab-duel-gamelist label {
    font-size: 1.1vw;
}
#tab-duel-gamelist p:not(.button) {
    font-size: 1vw;
    width: 100%;
}
#tab-duel-gamelist .table {
    font-size: 1.5vw;
    color: #30a791;
    border: 2px solid rgba(0, 0, 0, 0.1);
}
#tab-duel-gamelist .table::before {
    font-family: Arial;
    font-size: 1.25vw;
    content: "Ö Get Your Game On! Ö";
    color: antiquewhite;
    text-align: center;
    background: url("https://i62.servimg.com/u/f62/17/89/30/88/55637911.jpg") 0 0/100%; /*I have a morbid Moribuden for this one.*/
}

/*This affects the decks tab, but not the deck pages.*/
#tab-decks a {
    -moz-transition: color 1s ease-in-out, border 2s ease-in-out;
    -o-transition: color 1s ease-in-out, border 2s ease-in-out;
    transition: color 1s ease-in-out, border 2s ease-in-out;
    transition-property: all;
    font-size: 1vw;
    color: #C19E4D !important;
    text-shadow: 0px 1px 0.5px rgba(193, 158, 77, 0.6), 0px 0px 2px rgba(48, 167, 145, 1), 0px 1px 2px rgba(0, 0, 0, 0.8);
    background: radial-gradient(rgba(0, 0, 0, 0.1), rgba(0, 0, 0, 0));
    float: right;
}
#tab-decks a:focus, #tab-decks .button:focus {
    font-style: italic;
}
 #tab-decks a:hover {
    -moz-transition-duration: 1s;
    -o-transition-duration: 1s;
    transition-duration: 1s;
    transition-property: all;
    color: #30A791 !important;
    text-decoration: underline;
    text-shadow: 0px 0.5px 0.5px rgba(48, 167, 145, 0.4), 0px 0px 2px rgba(193, 158, 77, 0.8), 0px 0.5px 2px rgba(0, 0, 0, 0.6);
}
#tab-decks .button {
    font-size: 1.5vw;
    color: #C19E4D;
    text-shadow: 0 0 0.5vw rgba(0, 0, 0, 0.25), 0 0 0.5vw rgba(0, 0, 0, 0.25), 0 0 0.5vw rgba(0, 0, 0, 0.25);
    background-color: rgba(0, 0, 0, 0.75);
    border-color: rgba(193, 158, 77, 0.75);
    height: 2.5vw;
    width: 36vw !important;
    min-width: 0 !important;
    transform: rotate(-10deg);
    margin: 0;
    padding: 0;
    position: fixed !important;
}
#tab-decks .button:hover {
    color: antiquewhite;
    text-shadow: 0 0 0.5vw rgba(0, 0, 0, 0.75), 0 0 0.5vw rgba(0, 0, 0, 0.75), 0 0 0.5vw rgba(0, 0, 0, 0.75);
    background-color: rgba(192, 192, 192, 0.25);
}
#decks-create-button {
    float: left;
    top: 18vw;
    right: 0;
    bottom: 0;
    left: 16vw;
}
#tab-decks p.button {
    top: 15vw;
    right: 15vw;
}
#tab-decks .table {
    font-size: 1.5vw;
    color: #30a791;
    text-shadow: 0 0 0.25vw rgba(0, 0, 0, 0.5), 0 0 0.25vw rgba(0, 0, 0, 0.5), 0 0 0.25vw rgba(0, 0, 0, 0.5);
    border: 2px solid rgba(0, 0, 0, 0.1);
    margin-top: 25vw;
}
#tab-decks .table::before {
    font-family: Arial;
    font-size: 1.25vw;
    content: "Ö Now prepare to feel my wrath! Ö";
    color: antiquewhite;
    text-align: center;
    background: url("https://i62.servimg.com/u/f62/17/89/30/88/65591110.jpg") 0 0/100%; /*Here's a less morbid Moribuden.*/
}

/*This affects the profile tab, but not the profile pages. Er... if there were profile pages.*/
div#tab-profile {
    padding-bottom: 40px /*If your taskbar blocks the interface on the profile tab, increase this number.*/
}
#avatar_image {
    -moz-transition-duration: 1s;
    -o-transition-duration: 1s;
    transition-duration: 1s;
    border: 0.5vw solid rgba(193, 158, 77, 0.7);
    border-radius: 10%;
    box-shadow: 0px 0px 2px rgba(193, 158, 77, 0.7), 0px 0px 2px black;
    position: relative;
    right: 0.5vw;
}
#avatar_image:hover {
    -moz-transition-duration: 1s;
    -o-transition-duration: 1s;
    transition-duration: 1s;
    -moz-transform: rotateY(180deg);
    -o-transform: rotateY(180deg);
    transform: rotateY(180deg);
}
#avatar-previous-button, #avatar-next-button {
    width: 2vw !important;
    padding: 0;
    position: relative;
    right: 0.6vw;
}
#tab-profile p:not(.profile-if-not-contributor) {
    font-size: 1vw;
    color: antiquewhite;
}
#tab-profile .button { 
    color: #c19e4d;
    font-size: 1.25vw;
    text-shadow: 0 0 0.5vw rgba(0, 0, 0, 0.25), 0 0 0.5vw rgba(0, 0, 0, 0.25), 0 0 0.5vw rgba(0, 0, 0, 0.25);
    background-color: rgba(0, 0, 0, 0.75);
    border-color: rgba(193, 158, 77, 0.75);
    position: relative;
    right: 0.6vw;
}
#tab-profile .button:hover {
    color: antiquewhite;
    text-shadow: 0 0 0.5vw rgba(0, 0, 0, 0.75), 0 0 0.5vw rgba(0, 0, 0, 0.75), 0 0 0.5vw rgba(0, 0, 0, 0.75);
    background-color: rgba(192, 192, 192, 0.25);
}
#tab-profile p.profile-if-not-contributor {
    font-size: 0;
    height: 0;
    width: 0;
    padding: 0;
}
#tab-profile .row {
    background-color: rgba(0, 0, 0, 0.5) !important;
}
#profile-upload-avatar, #profile-upload-sleeve {
    width: auto;
    padding: 0.25vw 0.5vw;
}

/*This affects the siding screen, and the related elements of the deck pages.*/
body {
    font-family: Geneva, Ariel, sans-serif;
    line-height: 1;
}
button#game-siding-done {
    position: absolute;
    bottom: 0;
}
.editor-search-result {
    color: antiquewhite;
    background: radial-gradient(rgba(150, 150, 150, 0.5), rgba(50, 50, 50, 0.5));
